int parse_char_ID(char* str,unsigned char* ID){	
	 /* before modify*/
	char *endptr = NULL;
	char c;
	unsigned long int t_ID = 0;
	c = str[0];
	if (c>='0'&&c<='9'){
		t_ID=  strtoul(str,&endptr,10);
		if(t_ID < 256){
          *ID = (unsigned char)t_ID;
          if((c=='0')&&(str[1]!='\0'))
		  	return WID_UNKNOWN_ID;
		  else if(endptr[0] == '\0')
			return WID_DBUS_SUCCESS;
		  else
			return WID_UNKNOWN_ID;
		}
		else{
            return WID_ILLEGAL_INPUT;
		}
		
	}
	else
		return WID_UNKNOWN_ID;	
}

int parse_int_ID(char* str,unsigned int* ID){
	char *endptr = NULL;
	char c;
	c = str[0];
	if (c>='0'&&c<='9'){
		*ID= strtoul(str,&endptr,10);		
		if((endptr[0] == '\0')||(endptr[0] == '\n'))
			return WID_DBUS_SUCCESS;
		else
			return WID_UNKNOWN_ID;
	}
	else
		return WID_UNKNOWN_ID;
}


void str2lower(char **str) {  
	int i,len;
	char *ptr;

	len = strlen(*str);
	ptr = *str;

	for(i=0; i<len ; i++) {
		if((ptr[i]<='Z')&&(ptr[i]>='A'))  
			ptr[i] = ptr[i]-'A'+'a';  
	}
	
	return;
}


int Check_Time_Format(char* str){
	char *endptr = NULL;
	int endptr1 = 0;
	char c;
	int h,m,s;
	int time;
	c = str[0];
	if (c>='0'&&c<='9'){
		h= strtoul(str,&endptr,10);
		if(h < 0||h > 23)
			return -1;
		if(endptr[0] == '\0'||endptr[0] != ':')
			return -1;
		else{
			endptr1 = &endptr[1];
			m= strtoul(&endptr[1],&endptr,10);				
			if(m < 0||m > 59)
				return -1;				
		}
		
		if(endptr[0] == '\0'||endptr[0] != ':')
			return -1;
		else{
			endptr1 = &endptr[1];
			s = strtoul(&endptr[1],&endptr,10);				
			if(s < 0||s > 59)
				return -1;				
		}
		if(endptr[0] == '\0'){
			time = h*3600 + m*60 + s;
			return time;
		}
		else
			return -1;
	}
	else
		return -1;

}

CWBool_DCLI check_ascii_32_to126(const char * str)
{
	if(str == NULL)
	{
		return CW_TRUE_DCLI;
	}

	CWBool_DCLI ret = CW_TRUE_DCLI;
	const char *p = str;

	while(*p != '\0')
	{
		if((*p < 32 )||(*p > 126))
		{
			ret = CW_FALSE_DCLI;
			break;
		}
		p++;
	}
	return ret;
}

