	ccgi_ReInitDbusPath_v2(parameter.local_id, parameter.instance_id,WID_DBUS_WTP_OBJPATH,OBJPATH);
	ccgi_ReInitDbusPath_v2(parameter.local_id, parameter.instance_id,WID_DBUS_WTP_INTERFACE,INTERFACE);
