int parse_rate_list(char* ptr,int* count,int rate[])
{
	char* endPtr = NULL;
	int   i=0;
	endPtr = ptr;
	rate_list_state state = check_rate;

	while(1){
		switch(state)
		{
			
		case check_rate: 
			
				rate[i] = strtoul(endPtr,&endPtr,10);
			
				if(rate[i]>=10&&rate[i]<=540)
				{
            		state=check_comma;	
				}
				else
					state=check_fail;
				
				break;
		
		case check_comma: 
			
			if (RATE_SPLIT_COMMA == endPtr[0]){
				endPtr = (char*)endPtr + 1;
				state = check_rate;
			
				i++;
				if(i>=20) return -1;
				}
			else
				state = check_end;
			break;
				
		
		case check_fail:
	
			return -1;
			break;

		case check_end: 
	
			if ('\0' == endPtr[0]) {
				state = check_success;
				i++;
				if(i>=20) return -1;
				}
			else
				state = check_fail;
				break;
			
		case check_success: 

			*count = i;
			return 0;
			break;
			
		default: break;
		}
		
		}
		
}



